"""
This script searches all in-scope HTTP responses for JWTs. If a JWT is found, then it determines if one of the
HTTP request parameters is located inside the JWT.

Use this script to identify HTTP request parameters that might allow tampering the content of the JWT content.
"""
import re
import os
import json
import traceback

# Do the initial setup
if ref == 1 or "jwt_regex" not in session or "dedup" not in session:
	session["jwt_regex"] = re.compile("(?P<jwt>eyJ\w+\.eyJ\w+\.\w+)")
	header = ["Ref.", "URL", "Type", "Name", "Value", "JWT", "Path to JWT attribute"]
	session["dedup"] = {}

def get_jwts(content):
	"""
	This method implements the core functionality to extract information from requests or responses based on
	the given regular expressions.
	"""
	global session
	result = []
	for match in session["jwt_regex"].finditer(content):
		result.append(match.group("jwt"))
	return result

def search_json(content, search_string, path = "/"):
	"""
	This method recursively searches the given JSON object for the given value.
	"""
	result = []
	if isinstance(content, dict):
		for key, value in content.items():
			result += search_json(value, search_string, os.path.join(path, unicode(key)))
	elif isinstance(content, list):
		for item in content:
			result += search_json(item, search_string, path)
	elif search_string in content:
		result.append(unicode(path))
	return result

# Process only in-scope HTTP requests and responses
response = message_info.getResponse()
if in_scope and response:
	try:
		jwts = get_jwts(unicode(helpers.bytesToString(response)))
		for jwt in jwts:
			token = decode_jwt(jwt)
			jwt_payload = token[1]
			if jwt_payload:
				json_object = json.JSONDecoder().decode(jwt_payload)
				for parameter in request_info.getParameters():
					for path in search_json(json_object, parameter.getValue()):
						rows.append([ref, url, get_parameter_name(parameter.getType()), parameter.getName(), parameter.getValue(), jwt, path])
	except:
		traceback.print_exc(file=callbacks.getStderr())
